    //     .then(res => {
    //         setFormObject(res.data);
    //     })
    //     .catch(err => console.log(err));

    // }

    // useEffect(() => {
    //     loadActivities(topic);
    // }, [topic])

    // recent activity from topics user follows

    const [ dashObject, setDash ] = useState([]);

    useEffect(() => {
      loadTopics()
    }, [])

    function loadTopics() {
      API.getTopics()
        .then(res => 
          setDash(res.data)
        )
        .catch(err => console.log(err));
    };

    // useEffect(() => {

    //   API.getTopics()
    //   .then(res => {
    //     console.log(res.data)
    //     setDash(res.data);
    //   }).catch(err => err)

    // }, [dashObject]);


    return(
        <div className="animate__animated animate__fadeIn" >
        <ButtonGroup className="dash-container">
          {dashObject.map(data => {
            return (
                <Button
                  id="dash-button"
                  href={ data.local_ext }
                  bordered
                >
                  <img
                    id="dash-image"
                    src={data.img || "/images/no-image.png"}
                    alt={data.topic}
                  />
                  <p id="topic-title">{data.topic}</p>
                </Button>
            );

          })}
        </ButtonGroup>
        </div>
    )
}

export default DashActivity;
